				\usepackage{amssymb} 
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\:ETS=GN\:\times\:\:NR\:\times\:\:REC\:\times\:UZ$$\end{document}

